INSERT INTO `mhr_conf_it` (`id`, `table_id`, `table_name`) VALUES ('1', '1', '&lt;b&gt;Ingredienti per pagina.&lt;/b&gt;&lt;br/&gt;Indica il numero massimo di ingredienti mostrati per pagina nella pagina delle modifiche. Oltre questo numero verr&agrave; usata la lista alfabetica.'),('4', '4', '&lt;b&gt;Numero di piatti da mostrare nella top list.&lt;/b&gt;&lt;br/&gt;Es.: 3 -&gt; vengono mostrati i primi 3 piatti pi&ugrave; richiesti. (0 per disabilitare la visualizzazione della top list)'),('5', '5', '&lt;b&gt;Mostra le modifiche apportate nella righe di riepilogo.&lt;/b&gt;&lt;br/&gt;Se attivo, mostra tutte le modifiche anche nella riga dell\&#039;ultimo ordine modificato.'),('6', '6', '&lt;b&gt;Orario di chiusura del locale.&lt;/b&gt;&lt;br/&gt;Usato per decidere quando finisce la giornata contabile. Da immettere nel seguente formato: HHMMSS (Ore,minuti,secondi) senza spazi con gli zeri. Es: il locale chiude alle 3:30 del mattino, scrivere 033000. Usare il formato a 24 ore.'),('7', '7', '&lt;b&gt;Sistema di stampa.&lt;/b&gt;&lt;br/&gt;I sistemi di stampa disponibili ad oggi sono:&lt;br/&gt;lp o cupsys: per sistemi linux/unix&lt;br/&gt;win: per sistemi windows (non testato)'),('8', '8', '&lt;b&gt;Associazione automatica.&lt;/b&gt;&lt;br/&gt;Se attivo, al cameriere non viene richiesto se vuole associare un tavolo al suo primo ingresso, ma esso viene associato automaticamente.'),('9', '9', '&lt;b&gt;Permetti disassociazione.&lt;/b&gt;&lt;br/&gt;Se attivo, il cassiere pu&ograve; disassociare un tavolo da un cameriere.'),('10', '10', '&lt;b&gt;Autocalc considera le modifiche sottrattive.&lt;/b&gt;&lt;br/&gt;Se attivo, il sistema di calcolo automatico dei prezzi conteggia in negativo gli ingredienti rimossi, se no li ignora.'),('12', '12', '&lt;b&gt;Tempo di refresh generale.&lt;/b&gt;&lt;br/&gt;Indica il tempo in secondi prima che i messaggi di ok scompaiano in tutte le zone tranne quella camerieri. I messaggi di errore, invece, non scompaiono mai da soli.'),('13', '13', '&lt;b&gt;Ripetizione intestazioni colonne.&lt;/b&gt;&lt;br/&gt;Indica ogni quante righe si deve ripetere l\&#039;intestazione delle colonne nella zona contabilit&agrave;.'),('15', '15', '&lt;b&gt;Tempo di blocco tavolo.&lt;/b&gt;&lt;br/&gt;Tempo in secondi che deve intercorrere tra due operazioni sullo stesso tavolo ad parte di camerieri diversi. Se uguale a 0 il blocco &egrave; disabilitato.'),('16', '16', '&lt;b&gt;Lingua predefinita.&lt;/b&gt;&lt;br/&gt;Lingua usata in tutto il sistema, dove non altrimenti specificato (ad esempio nella zona camerieri, dove vale la lingua del singolo cameriere usato).'),('17', '17', '&lt;b&gt;Nome del fornitore del software.&lt;/b&gt;&lt;br/&gt;Usato nei messaggi di errore, per dare i contatti dell\&#039;assistenza.'),('18', '18', '&lt;b&gt;Indirizzo email del fornitore del software.&lt;/b&gt;&lt;br/&gt;Usato nei messaggi di errore, per dare i contatti dell\&#039;assistenza.'),('19', '19', '&lt;b&gt;Quantit&agrave; di default.&lt;/b&gt;&lt;br/&gt;Quantit&agrave; usata di default quando si prende un piatto nuovo.'),('21', '21', '&lt;b&gt;Stampa sempre i ticket rimanenti.&lt;/b&gt;&lt;br/&gt;Se attivo, alla chiusura del tavolo stampa tutti i ticket delle comande non ancora stampate.'),('22', '22', '&lt;b&gt;Stampa sempre i ticket rimanenti nei tavoli da asporto.&lt;/b&gt;&lt;br/&gt;Se attivo, alla chiusura dei tavoli da asporto stampa tutti i ticket delle comande non ancora stampate.'),('25', '25', '&lt;b&gt;Refresh automatico su lista tavoli.&lt;/b&gt;&lt;br/&gt;Indica ogni quanti secondi viene ricaricata la pagina della lista tavoli. (0 per disabilitare)'),('26', '26', '&lt;b&gt;Reindirizzamento automatico alla lista tavoli.&lt;/b&gt;&lt;br/&gt;Indica dopo quanti secondi di inattivit&agrave; si viene riportati alla lista dei tavoli. (0 per disabilitare)'),('30', '30', '&lt;b&gt;Mostra piatti e ingredienti invisibili.&lt;/b&gt;&lt;br/&gt;Se attivo, nella zona camerieri verranno visualizzati anche i piatti e gli ingredienti segnati come invisibili.'),('28', '28', '&lt;b&gt;Coperti - Prezzo&lt;/b&gt;&lt;br /&gt;Prezzo per persona del servizio in valuta. Non si pu&ograve; indicare un valore percentuale. (0 per non far pagare i coperti)'),('32', '32', '&lt;b&gt;Tavoli per riga (Cameriere)&lt;/b&gt;&lt;br/&gt;Il numero di tavoli per riga mostrati nella lista dei tavoli per i camerieri.'),('33', '33', '&lt;b&gt;Tavoli per riga (Cassiere)&lt;/b&gt;&lt;br/&gt;Il numero di tavoli per riga mostrati nella lista dei tavoli per i cassieri.'),('44', '45', '&lt;b&gt;Priorit&agrave; predefinita&lt;/b&gt;&lt;br/&gt;Priorit&agrave; da usare quando non c\&#039;&egrave; alcuna priorit&agrave; predefinita per categoria oppure nella lista dei piatti alfabetica.'),('35', '35', '&lt;b&gt;Mostra ordini cancellati.&lt;/b&gt;&lt;br/&gt;Se attivo. mostra gli ordini cancellati barrati da una linea al posto di nasconderli.'),('36', '36', '&lt;b&gt;Mostra la riga di riepilogo.&lt;/b&gt;&lt;br/&gt;Se attivo mostra nella pagina degli ordini una riga con l\&#039;ultimo ordine modificato.'),('37', '37', '&lt;b&gt;Coperti - Usa&lt;/b&gt;&lt;br /&gt;\r\nDecide se chiedere il numero di persone per tavolo.'),('38', '38', '&lt;b&gt;Permetti clienti sconosciuti.&lt;/b&gt;&lt;br/&gt;Se attivo, permette di inserire il cognome del cliente nei tavoli da asporto.'),('41', '42', '&lt;b&gt;Paese&lt;/b&gt;&lt;br/&gt;Paese da considerare per le impostazioni della valuta.'),('42', '43', '&lt;b&gt;Vai alle categorie dopo l\&#039;inserimento.&lt;/b&gt;&lt;br/&gt;Dopo aver creto un nuovo ordine attraverso linterfaccia delle categorie, torna alla stessa interfaccia per inserire nuovi ordini.'),('43', '44', '&lt;b&gt;Righe per pagina.&lt;/b&gt;&lt;br/&gt;Imposta il numero massimo di righe per tabella da mostrare in una singola pagina nella zona di gestione.');

